INSERT INTO `bz_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`) VALUES
(4, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2019-11-29 23:29:19', '0000-00-00 00:00:00', 0, '', '', 0),
(5, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2019-12-02 02:28:39', '0000-00-00 00:00:00', 0, '', '', 0),
(7, 'wc-admin-facebook-extension', 'info', 'en_US', 'Market on Facebook', 'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2019-12-02 23:55:56', '0000-00-00 00:00:00', 0, '', '', 0),
(8, 'wc-admin-orders-milestone', 'info', 'en_US', 'Congratulations on processing 10 orders!', 'You\'ve hit the 10 orders milestone! Look at you go. Browse some WooCommerce success stories for inspiration.', 'trophy', '{}', 'unactioned', 'woocommerce-admin', '2020-06-10 04:29:37', '0000-00-00 00:00:00', 0, '', '', 0),
(9, 'wc-update-db-reminder', 'update', 'en_US', 'WooCommerce database update required', 'WooCommerce has been updated! To keep things running smoothly, we have to update your database to the newest version. The database update process runs in the background and may take a little while, so please be patient. Advanced users can alternatively update via <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', 'info', '{}', 'unactioned', 'woocommerce-core', '2020-05-12 00:32:34', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(10, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://webdemo.com/w/hatdieu/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-11 00:28:12', '0000-00-00 00:00:00', 0, '', '', 0),
(11, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'We\'re here for you - get tips, product updates and inspiration straight to your email box', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-06-11 00:28:12', '0000-00-00 00:00:00', 0, '', '', 0),
(12, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', 'speaker', '{}', 'unactioned', 'woocommerce-admin', '2020-06-11 00:28:12', '0000-00-00 00:00:00', 0, '', '', 0),
(13, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Give feedback', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-08-14 23:34:05', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(15, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-08-14 23:34:05', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(17, 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, September 10th was your record day for sales! Net Sales was 1.600.000₫ beating the previous record of 1.500.000₫ set on August 30th.', 'info', '{\"old_record_date\":\"2020-08-30\",\"old_record_amt\":1500000,\"new_record_date\":\"2020-09-10\",\"new_record_amt\":1600000}', 'unactioned', 'woocommerce-admin', '2020-09-10 23:39:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(18, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-11-13 04:57:01', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(19, 'wc-admin-performance-on-mobile', 'info', 'en_US', 'Track your store performance on mobile', 'Monitor your sales and high performing products with the Woo app.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-11-28 06:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(20, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', 'info', '{}', 'pending', 'woocommerce.com', '2020-11-23 21:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(21, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', 'info', '{}', 'pending', 'woocommerce.com', '2020-11-23 21:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(22, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', 'info', '{}', 'pending', 'woocommerce.com', '2020-11-23 21:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(23, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'wcpay-promo-2020-11', 'wcpay-promo-2020-11', 'info', '{}', 'pending', 'woocommerce.com', '2020-11-26 05:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(24, 'wcpay-subscriptions-2020-11', 'marketing', 'en_US', 'Manage subscriber payments from your store\'s dashboard', 'Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>', 'info', '{}', 'pending', 'woocommerce.com', '2020-11-26 19:38:57', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(25, 'wc-admin-edit-products-on-the-move', 'info', 'en_US', 'Edit products on the move', 'Edit and create new products from your mobile devices with the Woo app', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-12-02 14:28:30', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(27, 'facebook_pixel_api_2021', 'marketing', 'en_US', 'Improve the performance of your Facebook ads', 'Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved performance and measurement of your Facebook ad campaigns.', 'info', '{}', 'pending', 'woocommerce.com', '2021-02-16 19:57:08', '0000-00-00 00:00:00', 0, 'plain', '', 0),
